function renderProducts(arr) {
    for (product of arr) {
        const productCard = document.createElement('div');
        productCard.classList.add('product-card');
    
        const img = document.createElement('img');
        img.setAttribute('src', product.image);
    
        const productInfo = document.createElement('div');
        productInfo.classList.add('productInfo');
        
        const productInfoDiv = document.createElement('div');
    
        const productPrice = document.createElement('p');
        productPrice.innerText = '$' + product.price;
        const productName = document.createElement('p');
        productName.innerText = product.name;
        
        const productInfoFigure = document.createElement('figure');
        const productImgCart = document.createElement('img');
        productImgCart.setAttribute('src', './icons/bt_add_to_cart.svg');
        
        
        /* Maquetacion del objeto en JS */
        
        cardsContainer.appendChild(productCard); /* Insertar todo a la raiz principal del HTML*/
        productCard.append(img, productInfo); /* Insertar la imagen del producto y la infor a la eqtiqueta ProductCard*/
        productInfo.append(productInfoDiv, productInfoFigure); /* Insertar la info y el icono del carrito a la etiqueta productInfo */
        productInfoDiv.append(productPrice, productName); /* Insertar el nomre y el precio al contenedor info */
        productInfoFigure.appendChild(productImgCart); /* Insertar la imagen a la etiqueta figure */
    }
}

renderProducts(productList);